import React, { useState, useCallback } from 'react'
import ConfigurationForm from './components/ConfigurationForm'
import PreviewGrid from './components/PreviewGrid'
import PageViewerModal from './components/PageViewerModal'
import { generatePages } from './utils/api'

function App() {
  const [pages, setPages] = useState([])
  const [isGenerating, setIsGenerating] = useState(false)
  const [selectedPage, setSelectedPage] = useState(null)
  const [error, setError] = useState(null)

  const handleGenerate = useCallback(async (config) => {
    setIsGenerating(true)
    setError(null)
    try {
      const generatedPages = await generatePages(config)
      setPages(generatedPages)
    } catch (err) {
      setError(err.message || 'Failed to generate pages')
      setPages([])
    } finally {
      setIsGenerating(false)
    }
  }, [])

  const handlePageClick = useCallback((page) => {
    setSelectedPage(page)
  }, [])

  const handleCloseModal = useCallback(() => {
    setSelectedPage(null)
  }, [])

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 py-6 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold text-gray-900">
            Style Generator
          </h1>
          <p className="mt-2 text-sm text-gray-600">
            Generate beautiful webpage designs with AI assistance
          </p>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 py-8 sm:px-6 lg:px-8">
        <div className="space-y-8">
          <ConfigurationForm 
            onGenerate={handleGenerate}
            isGenerating={isGenerating}
          />

          {error && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-4">
              <div className="flex">
                <div className="ml-3">
                  <h3 className="text-sm font-medium text-red-800">
                    Error
                  </h3>
                  <div className="mt-2 text-sm text-red-700">
                    <p>{error}</p>
                  </div>
                </div>
              </div>
            </div>
          )}

          <PreviewGrid 
            pages={pages}
            isGenerating={isGenerating}
            onPageClick={handlePageClick}
          />
        </div>
      </main>

      {selectedPage && (
        <PageViewerModal
          page={selectedPage}
          onClose={handleCloseModal}
        />
      )}
    </div>
  )
}

export default App

import React, { useState } from 'react'
import PreviewCard from './PreviewCard'
import { exportAllPages } from '../utils/api'

const PreviewGrid = ({ pages, isGenerating, onPageClick }) => {
  const [isExportingAll, setIsExportingAll] = useState(false)

  const handleExportAll = async () => {
    if (pages.length === 0) return

    setIsExportingAll(true)
    try {
      // Get session_id from the first page (all pages should have the same session_id)
      const sessionId = pages[0]?.session_id
      if (!sessionId) {
        throw new Error('Session ID not found')
      }

      await exportAllPages(sessionId)
    } catch (error) {
      console.error('Error exporting all pages:', error)
      alert('Failed to export all pages. Please try again.')
    } finally {
      setIsExportingAll(false)
    }
  }
  // Generate placeholder items for loading state
  const generatePlaceholders = () => {
    return Array.from({ length: 10 }, (_, index) => (
      <div
        key={`placeholder-${index}`}
        className="bg-white rounded-lg shadow-md overflow-hidden"
      >
        <div className="w-full h-48 bg-gray-200 animate-pulse relative">
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="text-gray-400 text-center">
              <div className="animate-spin h-8 w-8 border-2 border-gray-300 border-t-blue-500 rounded-full mx-auto mb-2"></div>
              <div className="text-sm">Generating...</div>
            </div>
          </div>
        </div>
        <div className="p-3">
          <div className="h-4 bg-gray-200 rounded animate-pulse mb-2"></div>
          <div className="h-3 bg-gray-200 rounded animate-pulse w-2/3"></div>
        </div>
      </div>
    ))
  }

  if (isGenerating) {
    return (
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-semibold text-gray-900">
            Generated Pages
          </h2>
          <div className="flex items-center text-blue-600">
            <svg className="animate-spin h-4 w-4 mr-2" fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
            </svg>
            <span className="text-sm font-medium">Generating pages...</span>
          </div>
        </div>

        <div className="grid grid-cols-2 lg:grid-cols-5 gap-4">
          {generatePlaceholders()}
        </div>
      </div>
    )
  }

  if (pages.length === 0) {
    return (
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-xl font-semibold text-gray-900 mb-6">
          Generated Pages
        </h2>
        <div className="text-center py-12">
          <div className="text-gray-400 mb-4">
            <svg className="mx-auto h-16 w-16" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="1.5" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
            </svg>
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No pages generated yet</h3>
          <p className="text-gray-600">
            Configure your preferences above and click "Generate Pages" to create your website designs.
          </p>
        </div>
      </div>
    )
  }

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-xl font-semibold text-gray-900">
          Generated Pages
        </h2>
        <div className="flex items-center space-x-4">
          <div className="text-sm text-gray-600">
            {pages.length} page{pages.length !== 1 ? 's' : ''} generated
          </div>
          {pages.length > 0 && (
            <button
              onClick={handleExportAll}
              disabled={isExportingAll}
              className="btn-primary text-sm flex items-center"
            >
              {isExportingAll ? (
                <>
                  <svg className="animate-spin h-4 w-4 mr-2" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Creating ZIP...
                </>
              ) : (
                <>
                  <svg className="h-4 w-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M9 19l3 3m0 0l3-3m-3 3V10" />
                  </svg>
                  Export All as ZIP
                </>
              )}
            </button>
          )}
        </div>
      </div>

      <div className="grid grid-cols-2 lg:grid-cols-5 gap-4">
        {pages.map((page, index) => (
          <PreviewCard
            key={page.id || `page-${index}`}
            page={page}
            index={index}
            onPageClick={onPageClick}
          />
        ))}
      </div>
    </div>
  )
}

export default PreviewGrid

import React, { useState, useEffect } from 'react'
import { saveAs } from 'file-saver'
import { getPageHtml } from '../utils/api'

const PageViewerModal = ({ page, onClose }) => {
  const [isExporting, setIsExporting] = useState(false)
  const [isLoading, setIsLoading] = useState(true)
  const [hasError, setHasError] = useState(false)
  const [fullHtml, setFullHtml] = useState(null)

  useEffect(() => {
    // Close modal on Escape key
    const handleKeyDown = (event) => {
      if (event.key === 'Escape') {
        onClose()
      }
    }

    document.addEventListener('keydown', handleKeyDown)
    document.body.style.overflow = 'hidden' // Prevent background scrolling

    // Load full HTML if page_id is available
    const loadFullHtml = async () => {
      if (page.page_id && !page.content) {
        try {
          const html = await getPageHtml(page.page_id)
          setFullHtml(html)
        } catch (error) {
          console.error('Error loading full HTML:', error)
          // Fall back to existing content
        }
      }
    }

    loadFullHtml()

    return () => {
      document.removeEventListener('keydown', handleKeyDown)
      document.body.style.overflow = 'unset'
    }
  }, [onClose, page])

  const handleIframeLoad = () => {
    setIsLoading(false)
    setHasError(false)
  }

  const handleIframeError = () => {
    setIsLoading(false)
    setHasError(true)
  }

  const handleExport = async () => {
    setIsExporting(true)
    try {
      // Use the API export endpoint for proper backend handling
      if (page.page_id) {
        const response = await fetch(`http://localhost:8001/api/export/${page.page_id}`)
        if (!response.ok) {
          throw new Error(`Export failed: ${response.statusText}`)
        }

        const htmlContent = await response.text()
        const blob = new Blob([htmlContent], { type: 'text/html;charset=utf-8' })
        const filename = page.filename || `${page.title?.replace(/\s+/g, '-').toLowerCase() || 'page'}.html`

        // Use file-saver to trigger download
        saveAs(blob, filename)
      } else {
        // Fallback to client-side export if no page_id
        const htmlContent = fullHtml || page.content || page.html_preview
        const blob = new Blob([htmlContent], { type: 'text/html;charset=utf-8' })
        const filename = page.filename || `${page.title?.replace(/\s+/g, '-').toLowerCase() || 'page'}.html`

        saveAs(blob, filename)
      }
    } catch (error) {
      console.error('Error exporting page:', error)
      alert('Failed to export page. Please try again.')
    } finally {
      setIsExporting(false)
    }
  }

  const handleBackdropClick = (event) => {
    if (event.target === event.currentTarget) {
      onClose()
    }
  }

  // Create a data URL for the iframe content
  const createDataUrl = (content) => {
    try {
      const encodedContent = encodeURIComponent(content)
      return `data:text/html;charset=utf-8,${encodedContent}`
    } catch (error) {
      console.error('Error creating data URL:', error)
      return null
    }
  }

  const iframeSrc = page.url || createDataUrl(fullHtml || page.content || page.html_preview)

  return (
    <div 
      className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-75 p-4"
      onClick={handleBackdropClick}
    >
      <div className="bg-white rounded-lg shadow-2xl max-w-6xl w-full h-[90vh] flex flex-col">
        {/* Modal Header */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <div className="flex items-center space-x-3">
            <h2 className="text-xl font-semibold text-gray-900">
              {page.title || 'Page Preview'}
            </h2>
            <span className="text-sm text-gray-500 bg-gray-100 px-2 py-1 rounded">
              {page.filename || 'preview.html'}
            </span>
          </div>

          <div className="flex items-center space-x-2">
            {/* Export Button */}
            <button
              onClick={handleExport}
              disabled={isExporting}
              className="btn-secondary text-sm flex items-center"
            >
              {isExporting ? (
                <>
                  <svg className="animate-spin h-4 w-4 mr-2" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Exporting...
                </>
              ) : (
                <>
                  <svg className="h-4 w-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 10v6m0 0l-3-3m3 3l3-3m2 8H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                  </svg>
                  Export HTML
                </>
              )}
            </button>

            {/* Close Button */}
            <button
              onClick={onClose}
              className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
            >
              <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
        </div>

        {/* Modal Content */}
        <div className="flex-1 relative overflow-hidden">
          {isLoading && (
            <div className="absolute inset-0 flex items-center justify-center bg-gray-50 z-10">
              <div className="text-center text-gray-500">
                <div className="animate-spin h-8 w-8 border-2 border-gray-300 border-t-blue-500 rounded-full mx-auto mb-3"></div>
                <div className="text-sm">Loading page preview...</div>
              </div>
            </div>
          )}

          {hasError ? (
            <div className="absolute inset-0 flex items-center justify-center bg-gray-50">
              <div className="text-center text-gray-500">
                <svg className="mx-auto h-12 w-12 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.664-.833-2.464 0L4.35 16.5c-.77.833.192 2.5 1.732 2.5z" />
                </svg>
                <h3 className="text-lg font-medium mb-2">Preview Unavailable</h3>
                <p className="text-sm text-gray-400 mb-4">Unable to display the page preview</p>
                <button
                  onClick={handleExport}
                  className="btn-primary text-sm"
                >
                  Download HTML File Instead
                </button>
              </div>
            </div>
          ) : (
            iframeSrc && (
              <iframe
                src={iframeSrc}
                className="w-full h-full border-0"
                onLoad={handleIframeLoad}
                onError={handleIframeError}
                sandbox="allow-same-origin allow-scripts"
                title={`Full preview of ${page.title || 'page'}`}
              />
            )
          )}
        </div>

        {/* Modal Footer */}
        <div className="p-4 border-t border-gray-200 bg-gray-50">
          <div className="flex justify-between items-center text-sm text-gray-600">
            <div>
              Press <kbd className="px-2 py-1 text-xs font-semibold text-gray-800 bg-gray-200 border border-gray-300 rounded">Esc</kbd> to close
            </div>
            <div>
              Full-size preview • Click Export to download HTML
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default PageViewerModal

import React, { useState } from 'react'

const PreviewCard = ({ page, index, onPageClick }) => {
  const [isLoading, setIsLoading] = useState(true)
  const [hasError, setHasError] = useState(false)

  const handleIframeLoad = () => {
    setIsLoading(false)
    setHasError(false)
  }

  const handleIframeError = () => {
    setIsLoading(false)
    setHasError(true)
  }

  const handleClick = () => {
    onPageClick(page)
  }

  // Create a data URL for the iframe content
  const createDataUrl = (content) => {
    try {
      // Encode the HTML content as a data URL
      const encodedContent = encodeURIComponent(content)
      return `data:text/html;charset=utf-8,${encodedContent}`
    } catch (error) {
      console.error('Error creating data URL:', error)
      return null
    }
  }

  const iframeSrc = page.url || createDataUrl(page.content || page.html_preview)

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300 cursor-pointer">
      <div 
        className="relative group"
        onClick={handleClick}
      >
        {/* Preview Container */}
        <div className="w-full h-48 bg-gray-100 relative overflow-hidden">
          {isLoading && (
            <div className="absolute inset-0 flex items-center justify-center bg-gray-50">
              <div className="text-center text-gray-400">
                <div className="animate-spin h-6 w-6 border-2 border-gray-300 border-t-blue-500 rounded-full mx-auto mb-2"></div>
                <div className="text-xs">Loading preview...</div>
              </div>
            </div>
          )}

          {hasError ? (
            <div className="absolute inset-0 flex items-center justify-center bg-gray-50">
              <div className="text-center text-gray-400">
                <svg className="mx-auto h-8 w-8 mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.664-.833-2.464 0L4.35 16.5c-.77.833.192 2.5 1.732 2.5z" />
                </svg>
                <div className="text-xs">Preview unavailable</div>
              </div>
            </div>
          ) : (
            iframeSrc && (
              <iframe
                src={iframeSrc}
                className="w-full h-full border-0 transform scale-[0.5] origin-top-left"
                style={{
                  width: '200%',
                  height: '200%',
                  pointerEvents: 'none'
                }}
                onLoad={handleIframeLoad}
                onError={handleIframeError}
                sandbox="allow-same-origin"
                title={`Preview of ${page.title || `Page ${index + 1}`}`}
              />
            )
          )}

          {/* Hover Overlay */}
          <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-30 transition-all duration-300 flex items-center justify-center">
            <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-300">
              <div className="bg-white rounded-full p-3 shadow-lg">
                <svg className="h-6 w-6 text-gray-700" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                </svg>
              </div>
            </div>
          </div>
        </div>

        {/* Card Footer */}
        <div className="p-3">
          <h3 className="font-medium text-gray-900 text-sm truncate">
            {page.title || `Page ${index + 1}`}
          </h3>
          <p className="text-xs text-gray-500 mt-1">
            {page.filename || `page-${index + 1}.html`}
          </p>
        </div>
      </div>
    </div>
  )
}

export default PreviewCard

