import axios from 'axios'

const API_BASE_URL = import.meta.env.VITE_API_URL || 'http://localhost:8001'

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 120000, // 2 minutes for generation
  headers: {
    'Content-Type': 'application/json',
  },
})

// Request interceptor for logging
api.interceptors.request.use(
  (config) => {
    console.log(`Making API request to ${config.url}`, config.data)
    return config
  },
  (error) => {
    console.error('API request error:', error)
    return Promise.reject(error)
  }
)

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => {
    return response
  },
  (error) => {
    console.error('API response error:', error)

    if (error.response?.status === 500) {
      throw new Error('Server error. Please try again later.')
    } else if (error.response?.status === 422) {
      // Handle validation errors more user-friendly
      const detail = error.response.data?.detail
      if (Array.isArray(detail) && detail.length > 0) {
        const fieldErrors = detail.map(err => `${err.loc?.join('.')}: ${err.msg}`).join(', ')
        throw new Error(`Validation error: ${fieldErrors}`)
      } else {
        throw new Error(detail || 'Invalid request parameters')
      }
    } else if (error.code === 'ECONNABORTED') {
      throw new Error('Request timeout. Please try again.')
    } else if (!error.response) {
      throw new Error('Unable to connect to server. Please check if the backend is running.')
    }

    throw error
  }
)

export const generatePages = async (config) => {
  try {
    // Transform frontend config to match backend API
    const backendConfig = {
      theme: config.theme,
      category: config.category || 'landing', // Default to landing if not specified
      palette: config.color_palette || config.palette, // Map color_palette to palette
      additional_requirements: config.additional_requirements || null
    }

    const response = await api.post('/api/generate', backendConfig)

    if (!response.data?.session_id) {
      throw new Error('Invalid response format from server')
    }

    const sessionId = response.data.session_id

    // Poll for completion and get pages
    return await pollForCompletion(sessionId)
  } catch (error) {
    console.error('Error generating pages:', error)
    throw error
  }
}

// Helper function to poll for completion
const pollForCompletion = async (sessionId, maxAttempts = 30) => {
  for (let attempt = 0; attempt < maxAttempts; attempt++) {
    try {
      const response = await api.get(`/api/pages/${sessionId}`)
      const sessionData = response.data

      if (sessionData.session.status === 'completed') {
        // Transform backend pages to frontend format and fetch full HTML for each
        const pagesWithFullHtml = await Promise.all(
          sessionData.pages.map(async (page, index) => {
            try {
              // Get full HTML content for proper display
              const fullHtml = await getPageHtml(page.page_id)
              return {
                id: page.page_id,
                filename: `${page.page_id}.html`,
                content: fullHtml, // Use full HTML for proper rendering
                url: null,
                title: page.title || `Page ${index + 1}`,
                page_id: page.page_id,
                session_id: sessionId
              }
            } catch (error) {
              console.error(`Error fetching HTML for page ${page.page_id}:`, error)
              // Fallback to preview if full HTML fetch fails
              return {
                id: page.page_id,
                filename: `${page.page_id}.html`,
                content: page.html_preview,
                url: null,
                title: page.title || `Page ${index + 1}`,
                page_id: page.page_id,
                session_id: sessionId
              }
            }
          })
        )
        return pagesWithFullHtml
      } else if (sessionData.session.status === 'failed') {
        throw new Error(sessionData.session.error_message || 'Page generation failed')
      }

      // Wait 2 seconds before next poll
      await new Promise(resolve => setTimeout(resolve, 2000))
    } catch (error) {
      if (error.response?.status === 404) {
        throw new Error('Generation session not found')
      }
      throw error
    }
  }

  throw new Error('Generation timeout - please try again')
}

export const exportPage = async (pageId, filename) => {
  try {
    const response = await api.get(`/api/export/${pageId}`, {
      responseType: 'blob',
    })

    // Create download link
    const blob = new Blob([response.data], { type: 'text/html' })
    const url = window.URL.createObjectURL(blob)
    const link = document.createElement('a')
    link.href = url
    link.download = filename || 'page.html'
    document.body.appendChild(link)
    link.click()
    document.body.removeChild(link)
    window.URL.revokeObjectURL(url)

    return true
  } catch (error) {
    console.error('Error exporting page:', error)
    throw error
  }
}

// Get full HTML for a specific page
export const getPageHtml = async (pageId) => {
  try {
    const response = await api.get(`/api/page/${pageId}`)
    return response.data.html
  } catch (error) {
    console.error('Error getting page HTML:', error)
    throw error
  }
}

export const exportAllPages = async (sessionId) => {
  try {
    const response = await api.post(`/api/export/all/${sessionId}`, {}, {
      responseType: 'blob',
    })

    // Create download link
    const blob = new Blob([response.data], { type: 'application/zip' })
    const url = window.URL.createObjectURL(blob)
    const link = document.createElement('a')
    link.href = url
    link.download = `pages-${sessionId}.zip`
    document.body.appendChild(link)
    link.click()
    document.body.removeChild(link)
    window.URL.revokeObjectURL(url)

    return true
  } catch (error) {
    console.error('Error exporting all pages:', error)
    throw error
  }
}

