class GenerateRequest(BaseModel):
    """Request model for generating HTML pages"""
    theme: constr(pattern=r'^[a-zA-Z0-9\s\-]{1,50}$') = Field(..., description="Theme for the HTML pages (e.g., 'modern', 'vintage', 'minimalist')")
    category: constr(pattern=r'^[a-zA-Z0-9\s\-]{1,50}$') = Field(..., description="Category of the pages (e.g., 'landing', 'portfolio', 'blog')")
    palette: constr(pattern=r'^[a-zA-Z0-9\s\-]{1,50}$') = Field(..., description="Color palette preference (e.g., 'warm', 'cool', 'monochrome')")
    additional_requirements: Optional[constr(max_length=500)] = Field(None, description="Additional specific requirements")

    @validator('theme', 'category', 'palette')
    def validate_no_special_chars(cls, v):
        """Prevent command injection by validating against dangerous characters"""
        if v and any(char in v for char in ['<', '>', '"', "'", '&', ';', '|', '$', '`', '\\', '/', '\n', '\r', '\t']):
            raise ValueError('Special characters and control sequences not allowed')
        return v

    @validator('additional_requirements')
    def validate_additional_requirements(cls, v):
        """Validate additional requirements for safety"""
        if v and any(char in v for char in ['<', '>', ';', '|', '$', '`', '\\', '\n', '\r']):
            raise ValueError('Special characters and control sequences not allowed in additional requirements')
        return v

    class Config:
        schema_extra = {
            "example": {
                "theme": "modern",
                "category": "landing",
                "palette": "cool",
