// Enhanced Palette API Functions

// Theme to category/tag mapping
const THEME_MAPPING = {
  'blues': { categories: ['cool'], tags: ['blue', 'cool'] },
  'monochrome': { categories: ['minimal'], tags: ['monochromatic', 'minimal'] },
  'dark': { categories: ['dark'], tags: ['dark'] },
  'light': { categories: ['minimal'], tags: ['light', 'bright'] },
  'warm': { categories: ['warm'], tags: ['warm'] },
  'cool': { categories: ['cool'], tags: ['cool'] },
  'pastel': { tags: ['pastel', 'soft', 'gentle'] }
}

export const getPaletteCategories = async () => {
  try {
    const response = await api.get('/api/palettes/categories')
    return response.data
  } catch (error) {
    console.error('Error fetching palette categories:', error)
    throw error
  }
}

export const getPalettesByTheme = async (theme) => {
  try {
    // Map frontend theme to backend parameters
    const mapping = THEME_MAPPING[theme]
    if (!mapping) {
      console.warn(`No mapping found for theme: ${theme}`)
      return []
    }

    const params = new URLSearchParams()

    if (mapping.categories) {
      params.append('categories', mapping.categories.join(','))
    }

    if (mapping.tags) {
      params.append('tags', mapping.tags.join(','))
    }

    // Limit to reasonable number for preview
    params.append('limit', '12')

    const response = await api.get(`/api/palettes?${params.toString()}`)
    return response.data.palettes || []
  } catch (error) {
    console.error(`Error fetching palettes for theme ${theme}:`, error)
    throw error
  }
}

export const getAllPalettes = async (options = {}) => {
  try {
    const params = new URLSearchParams()

    if (options.categories) {
      params.append('categories', Array.isArray(options.categories) ? options.categories.join(',') : options.categories)
    }

    if (options.tags) {
      params.append('tags', Array.isArray(options.tags) ? options.tags.join(',') : options.tags)
    }

    if (options.limit) {
      params.append('limit', options.limit)
    }

    if (options.offset) {
      params.append('offset', options.offset)
    }

    const response = await api.get(`/api/palettes?${params.toString()}`)
    return response.data
  } catch (error) {
    console.error('Error fetching all palettes:', error)
    throw error
  }
}

export const getPaletteById = async (paletteId) => {
  try {
    const response = await api.get(`/api/palettes/${paletteId}`)
    return response.data
  } catch (error) {
    console.error(`Error fetching palette ${paletteId}:`, error)
    throw error
  }
}

