import React from 'react'

const PaletteCard = ({ palette, onSelect, isSelected = false }) => {
  const handleClick = () => {
    if (onSelect) {
      onSelect(palette)
    }
  }

  const getAccessibilityBadgeColor = (score) => {
    if (score >= 70) return 'bg-green-100 text-green-800'
    if (score >= 50) return 'bg-yellow-100 text-yellow-800'
    return 'bg-red-100 text-red-800'
  }

  const getCategoryBadgeColor = (category) => {
    const colors = {
      professional: 'bg-blue-100 text-blue-800',
      minimal: 'bg-gray-100 text-gray-800',
      vibrant: 'bg-purple-100 text-purple-800',
      nature: 'bg-green-100 text-green-800',
      warm: 'bg-orange-100 text-orange-800',
      cool: 'bg-cyan-100 text-cyan-800',
      dark: 'bg-slate-100 text-slate-800',
      retro: 'bg-amber-100 text-amber-800'
    }
    return colors[category] || 'bg-gray-100 text-gray-800'
  }

  return (
    <div 
      className={`relative bg-white rounded-lg border-2 transition-all duration-200 cursor-pointer group hover:shadow-md ${
        isSelected ? 'border-blue-500 shadow-md ring-2 ring-blue-200' : 'border-gray-200 hover:border-gray-300'
      }`}
      onClick={handleClick}
    >
      {/* Selection indicator */}
      {isSelected && (
        <div className="absolute -top-2 -right-2 bg-blue-500 text-white rounded-full w-6 h-6 flex items-center justify-center z-10">
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
          </svg>
        </div>
      )}

      <div className="p-3">
        {/* Color swatches */}
        <div className="flex rounded-lg overflow-hidden mb-3 shadow-sm">
          {palette.colors?.map((color, index) => (
            <div
              key={index}
              className="flex-1 h-12 group-hover:h-14 transition-all duration-200"
              style={{ backgroundColor: color }}
              title={color}
            />
          ))}
        </div>

        {/* Palette info */}
        <div className="space-y-2">
          <div>
            <h4 className="font-medium text-gray-900 text-sm truncate">
              {palette.name}
            </h4>
          </div>

          {/* Category and scores */}
          <div className="flex items-center justify-between">
            <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getCategoryBadgeColor(palette.category)}`}>
              {palette.category}
            </span>

            {palette.accessibility_score !== undefined && (
              <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getAccessibilityBadgeColor(palette.accessibility_score)}`}>
                A11y: {Math.round(palette.accessibility_score)}
              </span>
            )}
          </div>

          {/* Color codes (show on hover) */}
          <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-200">
            <div className="text-xs text-gray-500 grid grid-cols-2 gap-1">
              {palette.colors?.slice(0, 4).map((color, index) => (
                <div key={index} className="font-mono">
                  {color}
                </div>
              ))}
            </div>
          </div>

          {/* Tags (compact) */}
          {palette.tags && palette.tags.length > 0 && (
            <div className="flex flex-wrap gap-1 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
              {palette.tags.slice(0, 3).map((tag, index) => (
                <span
                  key={index}
                  className="inline-flex items-center px-1.5 py-0.5 rounded text-xs bg-gray-100 text-gray-600"
                >
                  {tag}
                </span>
              ))}
              {palette.tags.length > 3 && (
                <span className="inline-flex items-center px-1.5 py-0.5 rounded text-xs bg-gray-100 text-gray-600">
                  +{palette.tags.length - 3}
                </span>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Hover overlay for better click indication */}
      <div className="absolute inset-0 bg-blue-500 opacity-0 hover:opacity-5 transition-opacity duration-200 rounded-lg" />
    </div>
  )
}

export default PaletteCard

