import React, { useState, useEffect } from 'react'
import PaletteCard from './PaletteCard'
import { getPalettesByTheme } from '../utils/api'

const PalettePreviewSection = ({ selectedTheme, onPaletteSelect, selectedPalette = null }) => {
  const [palettes, setPalettes] = useState([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  // Fetch palettes when theme changes
  useEffect(() => {
    const fetchPalettes = async () => {
      if (!selectedTheme) return

      setLoading(true)
      setError(null)

      try {
        const fetchedPalettes = await getPalettesByTheme(selectedTheme)
        setPalettes(fetchedPalettes)

        // If no palettes found, try to get some fallback palettes
        if (fetchedPalettes.length === 0) {
          console.warn(`No palettes found for theme: ${selectedTheme}`)
        }
      } catch (err) {
        console.error('Error fetching palettes:', err)
        setError('Failed to load color palettes. Please try again.')
        setPalettes([])
      } finally {
        setLoading(false)
      }
    }

    fetchPalettes()
  }, [selectedTheme])

  const handlePaletteSelect = (palette) => {
    if (onPaletteSelect) {
      onPaletteSelect(palette)
    }
  }

  const getThemeDisplayName = (theme) => {
    const themeNames = {
      'blues': 'Blues',
      'monochrome': 'Monochrome',
      'dark': 'Dark Mode',
      'light': 'Light Mode',
      'warm': 'Warm Tones',
      'cool': 'Cool Tones',
      'pastel': 'Pastel'
    }
    return themeNames[theme] || theme
  }

  // Loading skeleton component
  const LoadingSkeleton = () => (
    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
      {[...Array(8)].map((_, index) => (
        <div key={index} className="bg-white rounded-lg border-2 border-gray-200 p-3 animate-pulse">
          <div className="flex rounded-lg overflow-hidden mb-3">
            {[...Array(4)].map((_, colorIndex) => (
              <div key={colorIndex} className="flex-1 h-12 bg-gray-200" />
            ))}
          </div>
          <div className="space-y-2">
            <div className="h-4 bg-gray-200 rounded w-3/4" />
            <div className="flex justify-between">
              <div className="h-4 bg-gray-200 rounded w-1/3" />
              <div className="h-4 bg-gray-200 rounded w-1/4" />
            </div>
          </div>
        </div>
      ))}
    </div>
  )

  // Empty state component
  const EmptyState = () => (
    <div className="text-center py-12">
      <div className="text-gray-400 mb-4">
        <svg className="mx-auto h-16 w-16" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="1.5" d="M7 21a4 4 0 01-4-4V5a2 2 0 012-2h4a2 2 0 012 2v12a4 4 0 01-4 4zM21 5a2 2 0 00-2-2h-4a2 2 0 00-2 2v12a4 4 0 004 4h4a2 2 0 002-2V5z" />
        </svg>
      </div>
      <h3 className="text-lg font-medium text-gray-900 mb-2">No palettes found</h3>
      <p className="text-gray-600">
        We couldn't find any palettes for the "{getThemeDisplayName(selectedTheme)}" theme.
      </p>
    </div>
  )

  // Error state component
  const ErrorState = () => (
    <div className="text-center py-12">
      <div className="text-red-400 mb-4">
        <svg className="mx-auto h-16 w-16" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="1.5" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.664-.833-2.464 0L4.35 16.5c-.77.833.192 2.5 1.732 2.5z" />
        </svg>
      </div>
      <h3 className="text-lg font-medium text-red-900 mb-2">Error loading palettes</h3>
      <p className="text-red-600 mb-4">{error}</p>
      <button 
        onClick={() => window.location.reload()} 
        className="btn-primary text-sm"
      >
        Try Again
      </button>
    </div>
  )

  if (!selectedTheme) {
    return null
  }

  return (
    <div className="mt-6 bg-white rounded-lg shadow-md p-6">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">
            Available Palettes for "{getThemeDisplayName(selectedTheme)}"
          </h3>
          <p className="text-sm text-gray-600 mt-1">
            {loading ? (
              'Loading palettes...'
            ) : palettes.length > 0 ? (
              `${palettes.length} palette${palettes.length !== 1 ? 's' : ''} found`
            ) : (
              'Click a palette to use it for generation'
            )}
          </p>
        </div>

        {palettes.length > 0 && (
          <div className="text-sm text-gray-500">
            {selectedPalette && (
              <span className="text-blue-600 font-medium">
                "{selectedPalette.name}" selected
              </span>
            )}
          </div>
        )}
      </div>

      {/* Content */}
      {loading ? (
        <LoadingSkeleton />
      ) : error ? (
        <ErrorState />
      ) : palettes.length === 0 ? (
        <EmptyState />
      ) : (
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {palettes.map((palette) => (
            <PaletteCard
              key={palette.id}
              palette={palette}
              onSelect={handlePaletteSelect}
              isSelected={selectedPalette?.id === palette.id}
            />
          ))}
        </div>
      )}

      {/* Additional info */}
      {palettes.length > 0 && !loading && (
        <div className="mt-4 pt-4 border-t border-gray-200">
          <div className="text-xs text-gray-500 text-center">
            <p>Click any palette to select it for your page generation. Hover over palettes to see color codes and details.</p>
          </div>
        </div>
      )}
    </div>
  )
}

export default PalettePreviewSection

