import React, { useState } from 'react'
import PalettePreviewSection from './PalettePreviewSection'

const DESIGN_THEMES = [
  { value: 'modern', label: 'Modern' },
  { value: 'classic', label: 'Classic' },
  { value: 'minimalist', label: 'Minimalist' },
  { value: 'corporate', label: 'Corporate' },
  { value: 'creative', label: 'Creative' },
  { value: 'ecommerce', label: 'E-commerce' },
  { value: 'blog', label: 'Blog' },
  { value: 'portfolio', label: 'Portfolio' },
]

const CATEGORIES = [
  { value: '', label: 'Any Category (Optional)' },
  { value: 'technology', label: 'Technology' },
  { value: 'fashion', label: 'Fashion' },
  { value: 'food', label: 'Food' },
  { value: 'travel', label: 'Travel' },
  { value: 'business', label: 'Business' },
  { value: 'education', label: 'Education' },
  { value: 'health', label: 'Health' },
]

const COLOR_PALETTES = [
  { value: 'blues', label: 'Blues' },
  { value: 'monochrome', label: 'Monochrome' },
  { value: 'dark', label: 'Dark Mode' },
  { value: 'light', label: 'Light Mode' },
  { value: 'warm', label: 'Warm Tones' },
  { value: 'cool', label: 'Cool Tones' },
  { value: 'pastel', label: 'Pastel' },
]

const ConfigurationForm = ({ onGenerate, isGenerating }) => {
  const [config, setConfig] = useState({
    theme: 'modern',
    category: '',
    color_palette: 'blues',
  })

  const [selectedPalette, setSelectedPalette] = useState(null)

  const handleChange = (field, value) => {
    setConfig(prev => ({
      ...prev,
      [field]: value
    }))

    // Clear selected palette when color theme changes
    if (field === 'color_palette') {
      setSelectedPalette(null)
    }
  }

  const handlePaletteSelect = (palette) => {
    setSelectedPalette(palette)
  }

  const handleSubmit = (e) => {
    e.preventDefault()

    // Create clean config object (remove empty category)
    const cleanConfig = {
      theme: config.theme,
      color_palette: config.color_palette,
    }

    if (config.category) {
      cleanConfig.category = config.category
    }

    onGenerate(cleanConfig)
  }

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-xl font-semibold text-gray-900 mb-6">
        Configuration
      </h2>

      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Design Theme */}
          <div>
            <label htmlFor="theme" className="block text-sm font-medium text-gray-700 mb-2">
              Design Theme <span className="text-red-500">*</span>
            </label>
            <select
              id="theme"
              value={config.theme}
              onChange={(e) => handleChange('theme', e.target.value)}
              className="form-select"
              required
            >
              {DESIGN_THEMES.map((theme) => (
                <option key={theme.value} value={theme.value}>
                  {theme.label}
                </option>
              ))}
            </select>
          </div>

          {/* Category */}
          <div>
            <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-2">
              Category
              <span className="text-gray-500 text-xs ml-1">(optional)</span>
            </label>
            <select
              id="category"
              value={config.category}
              onChange={(e) => handleChange('category', e.target.value)}
              className="form-select"
            >
              {CATEGORIES.map((category) => (
                <option key={category.value} value={category.value}>
                  {category.label}
                </option>
              ))}
            </select>
          </div>

          {/* Color Palette */}
          <div>
            <label htmlFor="color_palette" className="block text-sm font-medium text-gray-700 mb-2">
              Color Palette <span className="text-red-500">*</span>
            </label>
            <select
              id="color_palette"
              value={config.color_palette}
              onChange={(e) => handleChange('color_palette', e.target.value)}
              className="form-select"
              required
            >
              {COLOR_PALETTES.map((palette) => (
                <option key={palette.value} value={palette.value}>
                  {palette.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Generate Button */}
        <div className="flex justify-center pt-4">
          <button
            type="submit"
            disabled={isGenerating}
            className="btn-primary px-8 py-3 text-lg relative"
          >
            {isGenerating ? (
              <>
                <span className="opacity-0">Generate Pages</span>
                <div className="absolute inset-0 flex items-center justify-center">
                  <svg className="animate-spin h-5 w-5 text-white mr-2" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Generating...
                </div>
              </>
            ) : (
              'Generate Pages'
            )}
          </button>
        </div>
      </form>

      {/* Configuration Summary */}
      <div className="mt-6 p-4 bg-gray-50 rounded-lg">
        <h3 className="text-sm font-medium text-gray-700 mb-2">Current Configuration:</h3>
        <div className="text-sm text-gray-600 space-y-1">
          <div>Theme: <span className="font-medium">{DESIGN_THEMES.find(t => t.value === config.theme)?.label}</span></div>
          <div>Category: <span className="font-medium">{config.category ? CATEGORIES.find(c => c.value === config.category)?.label : 'Any'}</span></div>
          <div>Color Palette: <span className="font-medium">{COLOR_PALETTES.find(p => p.value === config.color_palette)?.label}</span></div>
        </div>
      </div>
    </div>
  )
}

export default ConfigurationForm

