-- Taskasaurus Database Schema for Supabase
-- This creates the core tables for family chore management

-- Enable Row Level Security
ALTER DATABASE postgres SET "app.jwt_secret" TO 'your-jwt-secret-here';

-- Create custom types
CREATE TYPE user_role AS ENUM ('parent', 'child');
CREATE TYPE chore_status AS ENUM ('assigned', 'completed', 'approved', 'needs_redo');
CREATE TYPE redemption_status AS ENUM ('requested', 'approved', 'denied');

-- Families table
CREATE TABLE families (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    name VARCHAR(100) NOT NULL,
    family_code VARCHAR(10) UNIQUE NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Users table (family members)
CREATE TABLE users (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    family_id UUID NOT NULL REFERENCES families(id) ON DELETE CASCADE,
    name VARCHAR(50) NOT NULL,
    role user_role NOT NULL,
    points INTEGER DEFAULT 0,
    auth_id UUID REFERENCES auth.users(id), -- Supabase auth integration
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    UNIQUE(family_id, name) -- Unique names within family
);

-- Chores table
CREATE TABLE chores (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    family_id UUID NOT NULL REFERENCES families(id) ON DELETE CASCADE,
    title VARCHAR(100) NOT NULL,
    description TEXT,
    points INTEGER NOT NULL CHECK (points > 0),
    assigned_to UUID NOT NULL REFERENCES users(id),
    created_by UUID NOT NULL REFERENCES users(id),
    status chore_status DEFAULT 'assigned',
    due_date TIMESTAMP WITH TIME ZONE,
    completed_at TIMESTAMP WITH TIME ZONE,
    approved_at TIMESTAMP WITH TIME ZONE,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Rewards table
CREATE TABLE rewards (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    family_id UUID NOT NULL REFERENCES families(id) ON DELETE CASCADE,
    title VARCHAR(100) NOT NULL,
    description TEXT,
    cost INTEGER NOT NULL CHECK (cost > 0),
    created_by UUID NOT NULL REFERENCES users(id),
    is_active BOOLEAN DEFAULT TRUE,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Reward redemptions table
CREATE TABLE reward_redemptions (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    reward_id UUID NOT NULL REFERENCES rewards(id),
    user_id UUID NOT NULL REFERENCES users(id),
    points_spent INTEGER NOT NULL,
    status redemption_status DEFAULT 'requested',
    requested_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    processed_at TIMESTAMP WITH TIME ZONE,
    processed_by UUID REFERENCES users(id)
);

-- Indexes for performance
CREATE INDEX idx_families_family_code ON families(family_code);
CREATE INDEX idx_users_family_id ON users(family_id);
CREATE INDEX idx_users_auth_id ON users(auth_id);
CREATE INDEX idx_chores_family_id ON chores(family_id);
CREATE INDEX idx_chores_assigned_to ON chores(assigned_to);
CREATE INDEX idx_chores_status ON chores(status);
CREATE INDEX idx_rewards_family_id ON rewards(family_id);
CREATE INDEX idx_reward_redemptions_user_id ON reward_redemptions(user_id);
CREATE INDEX idx_reward_redemptions_status ON reward_redemptions(status);

-- Row Level Security (RLS) Policies
ALTER TABLE families ENABLE ROW LEVEL SECURITY;
ALTER TABLE users ENABLE ROW LEVEL SECURITY;
ALTER TABLE chores ENABLE ROW LEVEL SECURITY;
ALTER TABLE rewards ENABLE ROW LEVEL SECURITY;
ALTER TABLE reward_redemptions ENABLE ROW LEVEL SECURITY;

-- Families: Users can only see their own family
CREATE POLICY "Users can view their own family" ON families
    FOR SELECT USING (
        id IN (
            SELECT family_id FROM users 
            WHERE auth_id = auth.uid()
        )
    );

CREATE POLICY "Parents can update their family" ON families
    FOR UPDATE USING (
        id IN (
            SELECT family_id FROM users 
            WHERE auth_id = auth.uid() AND role = 'parent'
        )
    );

-- Users: Family members can see each other
CREATE POLICY "Family members can view each other" ON users
    FOR SELECT USING (
        family_id IN (
            SELECT family_id FROM users 
            WHERE auth_id = auth.uid()
        )
    );

CREATE POLICY "Parents can insert new family members" ON users
    FOR INSERT WITH CHECK (
        family_id IN (
            SELECT family_id FROM users 
            WHERE auth_id = auth.uid() AND role = 'parent'
        )
    );

CREATE POLICY "Users can update their own profile" ON users
    FOR UPDATE USING (auth_id = auth.uid());

-- Chores: Family members can see family chores
CREATE POLICY "Family members can view family chores" ON chores
    FOR SELECT USING (
        family_id IN (
            SELECT family_id FROM users 
            WHERE auth_id = auth.uid()
        )
    );

CREATE POLICY "Parents can create chores" ON chores
    FOR INSERT WITH CHECK (
        family_id IN (
            SELECT family_id FROM users 
            WHERE auth_id = auth.uid() AND role = 'parent'
        )
    );

CREATE POLICY "Assigned users can complete chores" ON chores
    FOR UPDATE USING (
        assigned_to IN (
            SELECT id FROM users WHERE auth_id = auth.uid()
        )
        OR created_by IN (
            SELECT id FROM users WHERE auth_id = auth.uid() AND role = 'parent'
        )
    );

-- Rewards: Family members can view family rewards
CREATE POLICY "Family members can view family rewards" ON rewards
    FOR SELECT USING (
        family_id IN (
            SELECT family_id FROM users 
            WHERE auth_id = auth.uid()
        )
    );

CREATE POLICY "Parents can manage rewards" ON rewards
    FOR ALL USING (
        family_id IN (
            SELECT family_id FROM users 
            WHERE auth_id = auth.uid() AND role = 'parent'
        )
    );

-- Reward redemptions: Users can view their own and parents can view all
CREATE POLICY "Users can view relevant redemptions" ON reward_redemptions
    FOR SELECT USING (
        user_id IN (SELECT id FROM users WHERE auth_id = auth.uid())
        OR user_id IN (
            SELECT u.id FROM users u
            JOIN users current_user ON current_user.family_id = u.family_id
            WHERE current_user.auth_id = auth.uid() AND current_user.role = 'parent'
        )
    );

CREATE POLICY "Users can create redemption requests" ON reward_redemptions
    FOR INSERT WITH CHECK (
        user_id IN (SELECT id FROM users WHERE auth_id = auth.uid())
    );

CREATE POLICY "Parents can update redemptions" ON reward_redemptions
    FOR UPDATE USING (
        user_id IN (
            SELECT u.id FROM users u
            JOIN users current_user ON current_user.family_id = u.family_id
            WHERE current_user.auth_id = auth.uid() AND current_user.role = 'parent'
        )
    );

-- Functions for updated_at timestamps
CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = NOW();
    RETURN NEW;
END;
$$ language 'plpgsql';

-- Triggers for updated_at
CREATE TRIGGER update_families_updated_at BEFORE UPDATE ON families FOR EACH ROW EXECUTE PROCEDURE update_updated_at_column();
CREATE TRIGGER update_users_updated_at BEFORE UPDATE ON users FOR EACH ROW EXECUTE PROCEDURE update_updated_at_column();
CREATE TRIGGER update_chores_updated_at BEFORE UPDATE ON chores FOR EACH ROW EXECUTE PROCEDURE update_updated_at_column();
CREATE TRIGGER update_rewards_updated_at BEFORE UPDATE ON rewards FOR EACH ROW EXECUTE PROCEDURE update_updated_at_column();

-- Function to generate unique family codes
CREATE OR REPLACE FUNCTION generate_family_code()
RETURNS TEXT AS $$
DECLARE
    code TEXT;
    done BOOLEAN := FALSE;
BEGIN
    WHILE NOT done LOOP
        -- Generate 6-character alphanumeric code
        code := UPPER(
            SUBSTRING(MD5(RANDOM()::TEXT) FROM 1 FOR 3) || 
            LPAD((RANDOM() * 999)::INT::TEXT, 3, '0')
        );

        -- Check if code already exists
        IF NOT EXISTS (SELECT 1 FROM families WHERE family_code = code) THEN
            done := TRUE;
        END IF;
    END LOOP;

    RETURN code;
END;
$$ LANGUAGE plpgsql;

-- Sample data for testing (optional - remove in production)
-- INSERT INTO families (name, family_code) VALUES ('Smith Family', generate_family_code());
-- INSERT INTO families (name, family_code) VALUES ('Johnson Family', generate_family_code());

-- Enable real-time subscriptions for all tables
ALTER PUBLICATION supabase_realtime ADD TABLE families;
ALTER PUBLICATION supabase_realtime ADD TABLE users;
ALTER PUBLICATION supabase_realtime ADD TABLE chores;
ALTER PUBLICATION supabase_realtime ADD TABLE rewards;
ALTER PUBLICATION supabase_realtime ADD TABLE reward_redemptions;

import type { Metadata } from "next";
import { Geist, Geist_Mono } from "next/font/google";
import "./globals.css";
import { QueryProvider } from "@/providers/query-provider";
import { AuthProvider } from "@/lib/hooks/use-auth";

const geistSans = Geist({
  variable: "--font-geist-sans",
  subsets: ["latin"],
  display: "swap",
});

const geistMono = Geist_Mono({
  variable: "--font-geist-mono",
  subsets: ["latin"],
  display: "swap",
});

export const metadata: Metadata = {
  title: "Taskasaurus - Family Task Management",
  description: "Modern family task and chore management app. Organize your family's tasks, assign chores, and track progress with a beautiful, user-friendly interface.",
  keywords: "family, tasks, chores, organization, productivity, family management",
  authors: [{ name: "Taskasaurus Team" }],
  creator: "Taskasaurus",
  publisher: "Taskasaurus",
  formatDetection: {
    email: false,
    address: false,
    telephone: false,
  },
  metadataBase: new URL(process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000'),
  openGraph: {
    title: "Taskasaurus - Family Task Management",
    description: "Modern family task and chore management app",
    type: "website",
    locale: "en_US",
  },
  twitter: {
    card: "summary_large_image",
    title: "Taskasaurus - Family Task Management",
    description: "Modern family task and chore management app",
  },
  appleWebApp: {
    capable: true,
    statusBarStyle: "default",
    title: "Taskasaurus",
  },
  manifest: "/manifest.json",
};

export const viewport = {
  width: "device-width",
  initialScale: 1,
  maximumScale: 1,
  userScalable: false,
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang="en" suppressHydrationWarning>
      <body
        className={`${geistSans.variable} ${geistMono.variable} antialiased min-h-screen bg-background`}
      >
        <QueryProvider>
          <AuthProvider>
            <div className="relative flex min-h-screen flex-col">
              <div className="flex-1">
                {children}
              </div>
            </div>
          </AuthProvider>
        </QueryProvider>
      </body>
    </html>
  );
}

'use client'

import { useState } from 'react'
import { Button } from "@/components/ui/button"
import { AuthGuard } from "@/components/auth/auth-guard"
import { UserAvatar } from "@/components/family/user-avatar"
import { FamilyOverview } from './family-overview'
import { MyTasks } from './my-tasks'
import { RewardsSection } from './rewards-section'
import { CreateChoreDialog } from './create-chore-dialog'
import { CreateRewardDialog } from './create-reward-dialog'
import { useAuth } from "@/lib/hooks/use-auth"
import { useDashboardData } from "@/lib/hooks/use-dashboard"
import { useRealTimeDashboard } from "@/lib/hooks/use-real-time"
import { DashboardSection } from "@/lib/types/dashboard"
import { cn } from "@/lib/utils"
import { 
  Search, 
  Bell, 
  LogOut, 
  Home,
  CheckSquare,
  Gift,
  Menu,
  X,
  AlertCircle,
  RefreshCw
} from "lucide-react"
import { Toaster } from 'sonner'

interface DashboardNavProps {
  currentSection: DashboardSection
  onSectionChange: (section: DashboardSection) => void
  pendingApprovals: number
  pendingRedemptions: number
  isParent: boolean
  isMobile: boolean
}

function DashboardNav({ 
  currentSection, 
  onSectionChange, 
  pendingApprovals, 
  pendingRedemptions,
  isParent,
  isMobile
}: DashboardNavProps) {
  const navItems = [
    {
      id: 'overview' as DashboardSection,
      label: 'Overview',
      icon: Home,
      badge: 0,
    },
    {
      id: 'tasks' as DashboardSection,
      label: 'Tasks',
      icon: CheckSquare,
      badge: isParent ? pendingApprovals : 0,
    },
    {
      id: 'rewards' as DashboardSection,
      label: 'Rewards',
      icon: Gift,
      badge: isParent ? pendingRedemptions : 0,
    },
  ]

  return (
    <nav className={cn(
      "space-y-1",
      isMobile ? "px-4 pb-4" : ""
    )}>
      {navItems.map((item) => {
        const Icon = item.icon
        const isActive = currentSection === item.id

        return (
          <button
            key={item.id}
            onClick={() => onSectionChange(item.id)}
            className={cn(
              "w-full flex items-center space-x-3 px-3 py-2 text-sm font-medium rounded-lg transition-colors",
              isActive 
                ? "bg-white/20 text-white" 
                : "text-white/80 hover:bg-white/10 hover:text-white"
            )}
          >
            <Icon className="h-5 w-5" />
            <span>{item.label}</span>
            {item.badge > 0 && (
              <span className="ml-auto bg-red-500 text-white text-xs px-2 py-0.5 rounded-full min-w-[20px] text-center">
                {item.badge}
              </span>
            )}
          </button>
        )
      })}
    </nav>
  )
}

function DashboardHeader({ 
  currentUser, 
  onSignOut, 
  isMobileMenuOpen, 
  onToggleMobileMenu,
  connectionStatus
}: {
  currentUser: {
    id: string
    name: string
    email?: string
    avatar_url?: string
  }
  onSignOut: () => void
  isMobileMenuOpen: boolean
  onToggleMobileMenu: () => void
  connectionStatus: 'connecting' | 'connected' | 'disconnected' | 'error'
}) {
  return (
    <header className="sticky top-0 z-50 w-full border-b border-white/20 backdrop-blur-md bg-white/80">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex h-16 items-center justify-between">
          <div className="flex items-center space-x-4">
            <Button
              variant="ghost"
              size="icon"
              className="md:hidden text-gray-700"
              onClick={onToggleMobileMenu}
            >
              {isMobileMenuOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
            </Button>
            <h1 className="text-2xl font-bold bg-gradient-to-r from-teal-600 to-cyan-600 bg-clip-text text-transparent">
              Taskasaurus
            </h1>
            {connectionStatus !== 'connected' && (
              <div className="flex items-center space-x-1 text-xs text-yellow-600">
                <div className="h-2 w-2 rounded-full bg-yellow-400 animate-pulse" />
                <span>{connectionStatus === 'connecting' ? 'Connecting...' : 'Reconnecting...'}</span>
              </div>
            )}
          </div>
          <div className="flex items-center space-x-2">
            <Button variant="ghost" size="icon">
              <Search className="h-5 w-5" />
            </Button>
            <Button variant="ghost" size="icon">
              <Bell className="h-5 w-5" />
            </Button>
            <UserAvatar
              userId={currentUser.id}
              name={currentUser.name}
              imageUrl={currentUser.avatar_url}
              size="sm"
            />
            <Button variant="ghost" size="icon" onClick={onSignOut}>
              <LogOut className="h-5 w-5" />
            </Button>
          </div>
        </div>
      </div>
    </header>
  )
}

function LoadingState() {
  return (
    <div className="min-h-screen gradient-teal-radial flex items-center justify-center">
      <div className="text-center text-white">
        <RefreshCw className="h-8 w-8 mx-auto mb-4 animate-spin" />
        <h2 className="text-xl font-semibold mb-2">Loading your family dashboard...</h2>
        <p className="text-white/80">Fetching the latest data</p>
      </div>
    </div>
  )
}

function ErrorState({ error, onRetry }: { error: Error | null, onRetry: () => void }) {
  return (
    <div className="min-h-screen gradient-teal-radial flex items-center justify-center p-4">
      <div className="text-center text-white max-w-md">
        <AlertCircle className="h-12 w-12 mx-auto mb-4 text-red-400" />
        <h2 className="text-xl font-semibold mb-2">Something went wrong</h2>
        <p className="text-white/80 mb-6">
          We couldn&apos;t load your dashboard. Please try again.
        </p>
        <div className="space-y-2">
          <Button onClick={onRetry} className="w-full">
            <RefreshCw className="h-4 w-4 mr-2" />
            Try Again
          </Button>
          <p className="text-xs text-white/60">
            {error?.message || 'Unknown error occurred'}
          </p>
        </div>
      </div>
    </div>
  )
}

function DashboardContent() {
  const auth = useAuth()
  const [currentSection, setCurrentSection] = useState<DashboardSection>('overview')
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false)

  // Get dashboard data - called unconditionally
  const { data, isLoading, error, refetch } = useDashboardData()

  // Set up real-time subscriptions - called unconditionally
  const { connectionStatus } = useRealTimeDashboard(
    auth.status === 'authenticated' ? auth.user.family_id : undefined
  )

  if (auth.status !== 'authenticated') {
    return null // AuthGuard will handle this
  }

  const { user } = auth
  const isParent = user.role === 'parent'

  // Mock family data for now - this would come from the API
  const mockFamily = {
    id: user.family_id || 'mock-family',
    name: 'The Johnson Family',
    family_code: 'FAM123',
    total_points: (data?.myChores || []).reduce((sum, chore) => sum + chore.points, 0),
  }

  const mockMembers = [
    {
      id: user.id,
      name: user.name,
      email: user.email,
      role: user.role,
      avatar_url: user.avatar_url,
      points: user.points,
      is_active: true,
      created_at: new Date().toISOString(),
      is_online: true,
      chores_completed_this_week: 5,
      points_earned_this_week: 150,
    }
  ]

  const mockStats = {
    active_chores: data?.myChores?.filter(c => c.status === 'assigned').length || 0,
    completed_chores_today: 2,
    completed_chores_this_week: 8,
    overdue_chores: data?.myChores?.filter(c => new Date(c.due_date) < new Date() && c.status === 'assigned').length || 0,
    total_points_earned_this_week: 240,
  }

  if (isLoading) {
    return <LoadingState />
  }

  if (error) {
    return <ErrorState error={error} onRetry={refetch} />
  }

  const pendingApprovalsCount = data?.pendingApprovals?.length || 0
  const pendingRedemptionsCount = data?.pendingRedemptions?.length || 0

  return (
    <div className="min-h-screen gradient-teal-radial">
      <DashboardHeader
        currentUser={user}
        onSignOut={auth.signOut}
        isMobileMenuOpen={isMobileMenuOpen}
        onToggleMobileMenu={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
        connectionStatus={connectionStatus}
      />

      <div className="flex">
        {/* Sidebar - Desktop */}
        <aside className="hidden md:flex md:w-64 md:flex-col">
          <div className="flex-1 p-6">
            <DashboardNav
              currentSection={currentSection}
              onSectionChange={setCurrentSection}
              pendingApprovals={pendingApprovalsCount}
              pendingRedemptions={pendingRedemptionsCount}
              isParent={isParent}
              isMobile={false}
            />
          </div>
        </aside>

        {/* Mobile Sidebar */}
        {isMobileMenuOpen && (
          <div className="fixed inset-0 z-40 md:hidden">
            <div className="fixed inset-0 bg-black/50" onClick={() => setIsMobileMenuOpen(false)} />
            <div className="fixed left-0 top-16 bottom-0 w-64 bg-gradient-to-b from-teal-600 to-cyan-700 p-6">
              <DashboardNav
                currentSection={currentSection}
                onSectionChange={(section) => {
                  setCurrentSection(section)
                  setIsMobileMenuOpen(false)
                }}
                pendingApprovals={pendingApprovalsCount}
                pendingRedemptions={pendingRedemptionsCount}
                isParent={isParent}
                isMobile={true}
              />
            </div>
          </div>
        )}

        {/* Main Content */}
        <main className="flex-1 px-4 sm:px-6 lg:px-8 py-8">
          {currentSection === 'overview' && (
            <FamilyOverview
              family={mockFamily}
              members={mockMembers}
              familyStats={mockStats}
            />
          )}

          {currentSection === 'tasks' && (
            <MyTasks
              currentUser={user}
              myChores={data?.myChores || []}
              familyChores={data?.familyChores || []}
              pendingApprovals={data?.pendingApprovals || []}
              onCreateChore={() => {
                // This would open the create chore dialog
              }}
            />
          )}

          {currentSection === 'rewards' && (
            <RewardsSection
              currentUser={user}
              availableRewards={data?.rewards || []}
              pendingRedemptions={data?.pendingRedemptions || []}
              myRedemptions={data?.myRedemptions || []}
              familyMembers={mockMembers}
              onCreateReward={() => {
                // This would open the create reward dialog
              }}
              onEditReward={(reward) => {
                // This would open the edit reward dialog
              }}
            />
          )}
        </main>
      </div>

      {/* Floating Action Buttons */}
      {isParent && (
        <div className="fixed bottom-6 right-6 space-y-3">
          {currentSection === 'tasks' && (
            <CreateChoreDialog
              familyMembers={mockMembers}
              trigger={
                <Button size="lg" className="shadow-lg">
                  <CheckSquare className="h-5 w-5 mr-2" />
                  New Task
                </Button>
              }
            />
          )}

          {currentSection === 'rewards' && (
            <CreateRewardDialog
              trigger={
                <Button size="lg" className="shadow-lg">
                  <Gift className="h-5 w-5 mr-2" />
                  New Reward
                </Button>
              }
            />
          )}
        </div>
      )}

      {/* Toast notifications with enhanced styling */}
      <Toaster 
        position="bottom-right"
        toastOptions={{
          style: {
            background: 'white',
            color: '#1f2937',
            border: '1px solid #e5e7eb',
            borderRadius: '12px',
            boxShadow: '0 10px 25px rgba(0, 0, 0, 0.1)',
            padding: '16px',
            fontSize: '14px',
          },
          duration: 4000,
        }}
        expand={true}
        richColors={true}
      />
    </div>
  )
}

export default function DashboardPage() {
  return (
    <AuthGuard>
      <DashboardContent />
    </AuthGuard>
  )
}

// Re-export auth hooks for backward compatibility
export {
  useAuth,
  useRequireAuth,
  useOptionalAuth,
  AuthProvider,
} from '@/lib/auth/auth-context'

# Taskasaurus Development and Testing Makefile
# Provides comprehensive commands for local development and testing

.PHONY: help install setup test test-local test-docker clean build dev prod deploy

# Default target
help:
	@echo "Taskasaurus Development Commands"
	@echo "================================"
	@echo ""
	@echo "Setup Commands:"
	@echo "  install          Install all dependencies (backend, frontend, e2e)"
	@echo "  setup            Complete project setup including database"
	@echo "  setup-db         Initialize PostgreSQL database"
	@echo ""
	@echo "Development Commands:"
	@echo "  dev              Start complete development environment"
	@echo "  dev-backend      Start backend development server only"
	@echo "  dev-frontend     Start frontend development server only"
	@echo "  dev-db           Start local database services (PostgreSQL + Redis)"
	@echo "  dev-docker       Start complete development environment in Docker"
	@echo ""
	@echo "Testing Commands:"
	@echo "  test             Run all tests (fastest option)"
	@echo "  test-local       Run tests using local database (no Docker)"
	@echo "  test-docker      Run complete test suite in Docker containers"
	@echo "  test-backend     Run backend tests only"
	@echo "  test-frontend    Run frontend tests only" 
	@echo "  test-e2e         Run E2E tests only"
	@echo "  test-coverage    Run tests with coverage reporting"
	@echo ""
	@echo "Database Commands:"
	@echo "  db-reset         Reset and reseed local database"
	@echo "  db-seed          Seed database with test data"
	@echo "  db-migrate       Run database migrations"
	@echo "  db-backup        Create database backup"
	@echo "  db-restore       Restore database from backup"
	@echo ""
	@echo "Docker Commands:"
	@echo "  build            Build all Docker images"
	@echo "  build-backend    Build backend Docker image"
	@echo "  build-frontend   Build frontend Docker image"
	@echo "  build-e2e        Build E2E testing image"
	@echo ""
	@echo "Utility Commands:"
	@echo "  clean            Clean up containers, volumes, and caches"
	@echo "  logs             View logs from all services"
	@echo "  logs-backend     View backend logs only"
	@echo "  logs-frontend    View frontend logs only"
	@echo "  shell-backend    Open shell in backend container"
	@echo "  shell-frontend   Open shell in frontend container"
	@echo "  health           Check health of all services"
	@echo "  validate         Validate local testing setup"
	@echo "  validate-docker  Validate Docker infrastructure setup"
	@echo "  quick-setup      Quick setup for developers"
	@echo "  quick-test       Quick test (backend + frontend only)"
	@echo ""
	@echo "Railway Deployment Commands:"
	@echo "  railway-setup    Set up Railway project and services"
	@echo "  railway-deploy   Deploy to Railway"
	@echo "  railway-logs     View Railway logs"
	@echo "  railway-env      Set Railway environment variables"
	@echo "  railway-db-init  Initialize Railway database"

# Installation and Setup
install:
	@echo "Installing backend dependencies..."
	cd backend && python -m pip install -r requirements.txt
	@echo "Installing frontend dependencies..."
	cd frontend && npm install
	@echo "Installing E2E test dependencies..."
	cd e2e-tests && npm install
	@echo "All dependencies installed successfully!"

setup: install setup-db
	@echo "Project setup complete!"

setup-db:
	@echo "Setting up local PostgreSQL database..."
	@command -v docker >/dev/null 2>&1 || { echo "Docker is required but not installed. Please install Docker first."; exit 1; }
	@echo "Starting database services..."
	docker-compose -f docker-compose.dev.yml up -d dev-db dev-redis
	@echo "Waiting for database to be ready..."
	@timeout 60s sh -c 'until docker-compose -f docker-compose.dev.yml exec -T dev-db pg_isready -U postgres; do echo "Waiting for database..."; sleep 2; done'
	@echo "Database setup complete!"

# Development Commands
dev:
	@echo "Starting complete development environment..."
	@make -j3 dev-db dev-backend dev-frontend

dev-backend:
	@echo "Starting backend development server..."
	cd backend && \
	export $$(cat .env | xargs) && \
	python -m uvicorn app.main:app --host 0.0.0.0 --port 8000 --reload

dev-frontend:
	@echo "Starting frontend development server..."
	cd frontend && \
	export NEXT_PUBLIC_API_URL=http://localhost:8000 && \
	npm run dev

dev-db:
	@echo "Starting local database services..."
	docker-compose -f docker-compose.dev.yml up -d dev-db dev-redis

dev-docker:
	@echo "Starting complete development environment in Docker..."
	docker-compose -f docker-compose.dev.yml up --build

# Testing Commands
test: test-local

test-local:
	@echo "Running local tests with database..."
	@make setup-db
	@echo "Running backend tests..."
	cd backend && \
	export $$(cat ../.env.test | xargs) && \
	python3 -m pytest tests/ -v --tb=short
	@echo "Running frontend tests..."
	cd frontend && \
	export $$(cat ../.env.test | xargs) && \
	npm test -- --watchAll=false
	@echo "Running E2E tests..."
	cd e2e-tests && \
	export $$(cat ../.env.test | xargs) && \
	npm test
	@echo "All local tests completed!"

test-docker:
	@echo "Running complete Docker test suite..."
	docker-compose -f docker-compose.test.yml up --build --abort-on-container-exit
	@make clean-test
	@echo "Docker test suite completed!"

test-backend:
	@echo "Running backend tests..."
	cd backend && \
	export $$(cat ../.env.test | xargs) && \
	python3 -m pytest tests/ -v --tb=short --disable-warnings

test-frontend:
	@echo "Running frontend tests..."
	cd frontend && \
	export $$(cat ../.env.test | xargs) && \
	npm test -- --watchAll=false

test-e2e:
	@echo "Running E2E tests..."
	cd e2e-tests && \
	export $$(cat ../.env.test | xargs) && \
	npm test

test-coverage:
	@echo "Running tests with coverage..."
	cd backend && \
	export $$(cat ../.env.test | xargs) && \
	python3 -m pytest tests/ -v --cov=app --cov-report=html --cov-report=term-missing
	cd frontend && \
	export $$(cat ../.env.test | xargs) && \
	npm test -- --watchAll=false --coverage

# Database Commands
db-reset:
	@echo "Resetting local database..."
	docker-compose -f docker-compose.dev.yml down dev-db
	docker-compose -f docker-compose.dev.yml up -d dev-db
	@echo "Waiting for database to be ready..."
	@timeout 60s sh -c 'until docker-compose -f docker-compose.dev.yml exec -T dev-db pg_isready -U postgres; do echo "Waiting for database..."; sleep 2; done'
	@echo "Database reset complete!"

db-seed:
	@echo "Seeding database with test data..."
	@make db-reset
	@echo "Database seeded!"

db-migrate:
	@echo "Running database migrations..."
	cd backend && \
	export $$(cat .env | xargs) && \
	python3 -c "import asyncio; from app.database.connection import init_database; asyncio.run(init_database())"
	@echo "Migrations complete!"

db-backup:
	@echo "Creating database backup..."
	@mkdir -p backups
	docker-compose -f docker-compose.dev.yml exec -T dev-db pg_dump -U postgres taskasaurus_dev > backups/backup_$$(date +%Y%m%d_%H%M%S).sql
	@echo "Backup created in backups/ directory"

db-restore:
	@echo "Available backups:"
	@ls -la backups/
	@read -p "Enter backup filename: " backup; \
	cat backups/$$backup | docker-compose -f docker-compose.dev.yml exec -T dev-db psql -U postgres taskasaurus_dev
	@echo "Database restored!"

# Docker Commands  
build:
	@echo "Building all Docker images..."
	@make -j3 build-backend build-frontend build-e2e

build-backend:
	@echo "Building backend Docker image..."
	docker build -f backend/Dockerfile.test -t taskasaurus-backend-test backend/

build-frontend:
	@echo "Building frontend Docker image..."
	docker build -f frontend/Dockerfile.test -t taskasaurus-frontend-test frontend/

build-e2e:
	@echo "Building E2E Docker image..."
	docker build -f e2e-tests/Dockerfile.test -t taskasaurus-e2e-test e2e-tests/

# Utility Commands
clean:
	@echo "Cleaning up Docker resources..."
	docker-compose -f docker-compose.test.yml down -v --remove-orphans
	docker system prune -f
	@make clean-test
	@echo "Cleanup complete!"

clean-test:
	@echo "Cleaning test artifacts..."
	rm -rf backend/.pytest_cache
	rm -rf backend/htmlcov
	rm -rf frontend/.jest-cache
	rm -rf frontend/coverage
	rm -rf e2e-tests/test-results
	rm -rf e2e-tests/screenshots
	rm -rf test-results
	@echo "Test artifacts cleaned!"

logs:
	@echo "Viewing logs from all services..."
	docker-compose -f docker-compose.test.yml logs -f

logs-backend:
	@echo "Viewing backend logs..."
	docker-compose -f docker-compose.test.yml logs -f backend-test

logs-frontend:
	@echo "Viewing frontend logs..."
	docker-compose -f docker-compose.test.yml logs -f frontend-test

shell-backend:
	@echo "Opening shell in backend container..."
	docker-compose -f docker-compose.test.yml exec backend-test /bin/bash

shell-frontend:
	@echo "Opening shell in frontend container..."
	docker-compose -f docker-compose.test.yml exec frontend-test /bin/sh

health:
	@echo "Checking health of all services..."
	@echo "Database Status:"
	@docker-compose -f docker-compose.dev.yml exec -T dev-db pg_isready -U postgres || echo "Database: DOWN"
	@echo ""
	@echo "Redis Status:"
	@docker-compose -f docker-compose.dev.yml exec -T dev-redis redis-cli ping || echo "Redis: DOWN"
	@echo ""
	@echo "Backend Status:"
	@curl -s http://localhost:8000/health || echo "Backend: DOWN"
	@echo ""
	@echo "Frontend Status:"
	@curl -s http://localhost:3000 || echo "Frontend: DOWN"

# Production Commands (prepare for deployment, DO NOT deploy)
prod-build:
	@echo "Building production Docker images..."
	docker build -t taskasaurus-backend-prod backend/
	docker build -t taskasaurus-frontend-prod frontend/
	@echo "Production images built. Ready for deployment."

prod-test:
	@echo "Testing production builds..."
	@echo "Starting production test environment..."
	docker run -d --name taskasaurus-prod-test -p 8080:8000 taskasaurus-backend-prod
	sleep 10
	curl -f http://localhost:8080/health || (echo "Production backend test failed" && exit 1)
	docker stop taskasaurus-prod-test
	docker rm taskasaurus-prod-test
	@echo "Production builds tested successfully!"

# Quick commands for developers
quick-test:
	@make test-backend && make test-frontend

quick-setup:
	@make install && make setup-db

validate:
	@echo "Validating local testing setup..."
	python3 validate-local-setup.py

validate-docker:
	@echo "Validating Docker infrastructure setup..."
	python3 validate-docker-setup.py

# Railway Deployment Commands
railway-setup:
	@echo "Setting up Railway project..."
	@command -v railway >/dev/null 2>&1 || { echo "Installing Railway CLI..."; npm install -g @railway/cli; }
	@echo "Logging in to Railway (will open browser)..."
	railway login
	@echo "Creating Railway project..."
	railway link || railway new taskasaurus
	@echo "Adding PostgreSQL service..."
	railway add postgresql
	@echo "Adding Redis service..."
	railway add redis
	@echo "Railway project setup complete!"
	@echo "Next: Set environment variables with 'make railway-env'"

railway-env:
	@echo "Setting Railway environment variables..."
	@echo "Setting JWT_SECRET..."
	@read -p "Enter JWT_SECRET (min 32 characters): " jwt_secret; \
	railway variables set JWT_SECRET="$$jwt_secret"
	@echo "Setting other environment variables..."
	railway variables set JWT_ALGORITHM=HS256
	railway variables set ACCESS_TOKEN_EXPIRE_MINUTES=30
	railway variables set ENVIRONMENT=production
	railway variables set LOG_LEVEL=INFO
	@read -p "Enter frontend domain for CORS (e.g., https://app.taskasaurus.com): " cors_origin; \
	railway variables set CORS_ORIGINS="$$cors_origin"
	@echo "Environment variables set! Verify with: railway variables"

railway-deploy:
	@echo "Deploying to Railway..."
	@echo "Building production images first..."
	@make prod-build
	@echo "Deploying backend..."
	cd backend && railway up --detach
	@echo "Deployment initiated! Monitor with: railway logs"
	@echo "Check deployment status: railway status"

railway-logs:
	@echo "Viewing Railway logs..."
	railway logs

railway-status:
	@echo "Checking Railway deployment status..."
	railway status

railway-db-init:
	@echo "Initializing Railway database..."
	@echo "Connecting to Railway PostgreSQL and running initialization..."
	railway connect postgresql -c "\\i database/init.sql"
	@echo "Adding seed data..."
	railway connect postgresql -c "\\i database/seed.sql"
	@echo "Railway database initialized!"
